import { createFeatureSelector, createSelector } from '@ngrx/store';
export const selectHomeState = createFeatureSelector('home');
export const selectCurrentWeather = createSelector(selectHomeState, homeState => homeState.entity);
export const selectCurrentWeatherLoading = createSelector(selectHomeState, homeState => homeState.loading);
export const selectCurrentWeatherError = createSelector(selectHomeState, homeState => homeState.error);
