export class CustomRouterSerializer {
  serialize(routerState) {
    const {
      url
    } = routerState;
    const {
      queryParams
    } = routerState.root;
    const {
      fragment
    } = routerState.root;
    let route = routerState.root;
    const params = {};

    do {
      if (!!route.params) {
        Object.keys(route.params).forEach(key => {
          params[key] = route.params[key];
        });
      }

      route = route.firstChild;
    } while (!!route);

    return {
      url,
      params,
      queryParams,
      fragment
    };
  }

}
